import React, { Component } from 'react'
import jQuery from 'jquery';
import './Navbar.scss';


export default class GrcNavbar extends Component {
  componentDidMount () {
    const triggers = document.querySelectorAll(".navList > li");
const background = document.querySelector(".dropdownBackground");
const nav = document.querySelector(".navWrapper");

function handleEnter() {
  this.classList.add("trigger-enter");
  setTimeout(
    () =>
      this.classList.contains("trigger-enter") &&
      this.classList.add("trigger-enter-active"),
    150
  );
  background.classList.add("open");

  const dropdown = this.querySelector(".dropdown");
  const dropdownCoords = dropdown.getBoundingClientRect();
  const navCoords = nav.getBoundingClientRect();

  const coords = {
    height: dropdownCoords.height,
    width: dropdownCoords.width,
    top: dropdownCoords.top - navCoords.top,
    left: dropdownCoords.left - navCoords.left
  };

  background.style.setProperty("width", `${coords.width}px`);
  background.style.setProperty("height", `${coords.height}px`);
  background.style.setProperty(
    "transform",
    `translate(${coords.left}px, ${coords.top}px)`
  );
}

function handleLeave() {
  this.classList.remove("trigger-enter", "trigger-enter-active");
  background.classList.remove("open");
}

triggers.forEach(trigger =>
  trigger.addEventListener("mouseenter", handleEnter)
);
triggers.forEach(trigger =>
  trigger.addEventListener("mouseleave", handleLeave)
);

  }

  render() {
    return (
      <div>
        <header className="custom-dropdown">
          <a href="#0" className="nav-trigger">Open Nav<span aria-hidden="true" /></a>
          <nav className="main-nav">
            <ul>
              <li className="has-dropdown gallery" data-content="about">
                <a href="/about">About</a>
              </li>
              <li className="has-dropdown gallery" data-content="services">
                <a href="#0">Services</a>
              </li>
              <li className="has-dropdown links" data-content="pricing">
                <a href="#0">Pricing</a>
              </li>
              <li className="has-dropdown button" data-content="user">
                <a href="#0">user</a>
              </li>
            </ul>
          </nav>
          <div className="morph-dropdown-wrapper">
            <div className="dropdown-list">
              <ul>
                <li id="about" className="dropdown">
                  <a href="/" className="label">About</a>
                </li>
                <li id="services" className="dropdown">
                  <a href="#0" className="label">Services</a>
                  <div className="content">	
                    <p>Services menu section goes here</p>
                  </div>
                </li>
                <li id="pricing" className="dropdown">
                  <a href="#0" className="label">Pricing</a>
                  <div className="content">
                    <p>Pricing menu goes here</p>
                  </div>
                </li>
                <li id="user" className="dropdown">
                  <a href="#0" className="label">User</a>
                  <div className="content">
                    <p>Users menu section goes here</p>
                  </div>
                </li>
              </ul>
              <div className="bg-layer" aria-hidden="true" />
            </div>
          </div>
        </header>
      </div>
    )
  }
}
