import React, { Component } from 'react'
import jQuery from 'jquery';
import './Navbar.scss';


export default class GrcNavbar extends Component {
  componentDidMount () {
    const triggers = document.querySelectorAll(".navList > li");
const background = document.querySelector(".dropdownBackground");
const nav = document.querySelector(".navWrapper");

function handleEnter() {
  this.classList.add("trigger-enter");
  setTimeout(
    () =>
      this.classList.contains("trigger-enter") &&
      this.classList.add("trigger-enter-active"),
    150
  );
  background.classList.add("open");

  const dropdown = this.querySelector(".dropdown");
  const dropdownCoords = dropdown.getBoundingClientRect();
  const navCoords = nav.getBoundingClientRect();

  const coords = {
    height: dropdownCoords.height,
    width: dropdownCoords.width,
    top: dropdownCoords.top - navCoords.top,
    left: dropdownCoords.left - navCoords.left
  };

  background.style.setProperty("width", `${coords.width}px`);
  background.style.setProperty("height", `${coords.height}px`);
  background.style.setProperty(
    "transform",
    `translate(${coords.left}px, ${coords.top}px)`
  );
}

function handleLeave() {
  this.classList.remove("trigger-enter", "trigger-enter-active");
  background.classList.remove("open");
}

triggers.forEach(trigger =>
  trigger.addEventListener("mouseenter", handleEnter)
);
triggers.forEach(trigger =>
  trigger.addEventListener("mouseleave", handleLeave)
);

  }

  render() {
    return (
      <div>
        <nav className="navWrapper">
        <div className="dropdownBackground">
          <span className="arrow" />
        </div>
        <ul className="navList">
          <li>
            <a href="#">Menu Item 1</a>
            <div className="dropdown dropdownText">
              <p>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, no sea takimata sanctus est Lorem ipsum dolor sit amet.</p>
            </div>
          </li>
          <li>
            <a href="#">Menu Item 2</a>
            <ul className="dropdown dropdownList">
              <li>
                <a href="#">Link 1</a>
              </li>
              <li>
                <a href="#">Link 2</a>
              </li>
              <li>
                <a href="#">Link 3</a>
              </li>
              <li>
                <a href="#">Link 4</a>
              </li>
              <li>
                <a href="#">Link 5</a>
              </li>
              <li>
                <a href="#">Link 6</a>
              </li>
              <li>
                <a href="#">Link 7</a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">Menu Item 3</a>
            <ul className="dropdown dropdownButtons">
              <li><a className="button" href="#">Twitter</a></li>
              <li><a className="button" href="#">Facebook</a></li>
              <li><a className="button" href="#">Blog</a></li>
              <li><a className="button" href="#">Course Catalog</a></li>
            </ul>
          </li>
        </ul>
      </nav>
      </div>
    )
  }
}
