import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './Navbar.scss';
import $ from 'jquery';

import { Link } from 'react-router-dom';

export default class GrcNavbar extends Component {
  componentDidMount () {
    $("#navbarMenu ul .submenu > a").append('<span class="submenu-button"></span>');

//OPEN TOGGLE MENU ON BREAKPOINT MEDIA QUERY
$(".toggleMenu").click(function(e){
    $("nav > ul").slideToggle();
    e.preventDefault();
});

//TO PREVENT MENU CLOSE ON CLICK IN SUBMENU
$(".submenu ul li").click(function(e){
    e.stopPropagation();
});

//CLICK ON SUBMENU LINK
$(".submenu").click(function(e){
    //CLOSE OPEN SUBMENU
    if($(this).hasClass("active")){
        $("#navbarMenu ul li").removeClass('active');
        $('#navbarMenu ul li ul').slideUp();
    }else{
        $("#navbarMenu ul li").removeClass('active');
        $("#navbarMenu ul li ul").slideUp();
        $(this).addClass("active");
        $(this).find('ul').stop().slideToggle();
        e.stopPropagation();
    }
    $("#navbarMenu ul li").removeClass('openSub');
    $(this).addClass('openSub');

});

$("body, html").click(function(){
    $("#navbarMenu ul li").removeClass('active openSub');
    $('#navbarMenu ul li ul').slideUp();
});
  }
  render() {
    return (
      <div className="container">
        <nav id="navbarMenu">
          <div className="toggleMenu">Menu</div>
          {/* <div id="brand">COMPANY</div> */}
          <ul>
            <li><a href="#">Home</a></li>
            <li><a href="#">About</a></li>
            <li className="submenu"><a href="#">Tutorials</a>
              <ul>
                <li><a href="#">Photoshop</a></li>
                <li><a href="#">CSS</a></li>
              </ul>
            </li>
            <li className="submenu"><a href="#">Articles</a>
              <ul>
                <li><a href="#">Web Design</a></li>
                <li><a href="#">User Experience</a></li>
              </ul>
            </li>
            <li><a href="#">Inspiration</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </nav>
      </div>
    )
  }
}
