import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import './Home.scss';
import { TweenLite, Power1 } from 'gsap';
export default class Home extends Component {

    componentDidMount(){
        var $window = $(window),
    $mainContainer = $('.container'),
    $perspectiveContainer = $('.perspective-container'),
    $contentContainer = $('.content-container'),
    windowWidth = $window.innerHeight(),
    windowHeight = $window.innerWidth(),
    halfWidth = windowWidth / 2,
    halfHeight = windowHeight / 2,
    maxYRotation = 8,
    maxXRotation = 5;

function resize() {
  windowWidth = $window.innerHeight();
  windowHeight = $window.innerWidth();
  halfWidth = windowWidth / 2;
  halfHeight = windowHeight / 2;

  init();
}

function animationFrame(e) {
  window.requestAnimationFrame(function() {
    rotateLayers(e, $perspectiveContainer);
  });
}

function init() {
  $perspectiveContainer.css({
    'max-height': 2 * windowWidth
  });
  $contentContainer.css({
    height: windowWidth
  });
}

function rotateLayers(e, element) {
  var rotationX,
      rotationY;

  rotationY = (-e.pageX + halfWidth) / halfWidth * maxYRotation;
  rotationX = (e.pageY - halfHeight) / halfHeight * maxXRotation;

  if (rotationY > maxYRotation) {
    rotationY = maxYRotation;
  } else if (rotationY < -maxYRotation) {
    rotationY = -maxYRotation;
  }

  if (rotationX > maxXRotation) {
    rotationX = maxXRotation;
  } else if (rotationX < -maxXRotation) {
    rotationX = -maxXRotation;
  }

  TweenLite.to(element, .8, {
    rotationX: rotationX,
    rotationY: rotationY,
    ease: Power1.easeOut
  });
}

init();

$window.on('resize', resize);
$mainContainer.on('mousemove', animationFrame);
    }
    render() {

        return (
            <div>
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/background.jpg" alt="New York" className="background" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/1.png" alt="Clouds" className="cloud cloud1" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/2.png" alt="Clouds" className="cloud cloud2" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/3.png" alt="Clouds" className="cloud cloud3" />
      </div>
        )
    }
}
