import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import './Home.scss';
import { TweenLite } from 'gsap';
export default class Home extends Component {

    componentDidMount(){
        $.fn.parallax = function(resistance, mouse) {
        $el = $(this);
        TweenLite.to($el, 0.2, {
          x: -((mouse.clientX - window.innerWidth / 2) / resistance),
          y: -((mouse.clientY - window.innerHeight / 2) / resistance)
        });
      };

      $(document).mousemove(function(e) {
        $(".background").parallax(-30, e);
        $(".cloud1").parallax(10, e);
        $(".cloud2").parallax(20, e);
        $(".cloud3").parallax(30, e);
        });}
    render() {

        return (
            <div>
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/background.jpg" alt="New York" className="background" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/1.png" alt="Clouds" className="cloud cloud1" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/2.png" alt="Clouds" className="cloud cloud2" />
        <img src="http://www.jqueryscript.net/demo/Mouse-aware-Parallax-Effect-with-jQuery-and-TweenMax-Mouse-Parallax/images/3.png" alt="Clouds" className="cloud cloud3" />
      </div>
        )
    }
}
